package main
import \("fmt"; "time"\)
func main\(\) {
    // What the DB should have: Jan 5, 2026, 11:30 AM CET \(+01:00\)
    loc, _ := time.LoadLocation\("Europe/Amsterdam"\)
    correct := time.Date\(2026, 1, 5, 11, 30, 0, 0, loc\)
    fmt.Printf\("Correct time: %s\\n", correct.Format\(time.RFC3339\)\)
    fmt.Printf\("Correct Unix: %d\\n", correct.Unix\(\)\)

    // What Go is reading: Jan 1, 2026, 12:30 PM UTC
    wrong := time.Date\(2026, 1, 1, 12, 30, 0, 0, time.UTC\)
    fmt.Printf\("\\nWhat Go reads: %s\\n", wrong.Format\(time.RFC3339\)\)
    fmt.Printf\("Wrong Unix: %d\\n", wrong.Unix\(\)\)
}
EOF)
